    private void AIActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AIActionPerformed
       String Tempa = IntTF.getText();
       String Tempb = DITF.getText();
       int a = Integer.parseInt(Tempa);
       int b = Integer.parseInt(Tempb);
       int sum = a + b;
       IntTF.setText(Integer.toString(sum));
       // TODO add your handling code here:
    }//GEN-LAST:event_AIActionPerformed

