    private void PowIActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PowIActionPerformed
       String Tempa = IntTF.getText();
       String Tempb = DITF.getText();
       int a = Integer.parseInt(Tempa);
       int b = Integer.parseInt(Tempb);
       int sum = (int)Math.pow(a, b);
       IntTF.setText(Integer.toString(sum));
// TODO add your handling code here:
    }//GEN-LAST:event_PowIActionPerformed

